import React, { useState } from "react";
import { AiOutlineClose } from "react-icons/ai";
import { useFormik } from "formik";
import * as Yup from "yup";
import "./movieform.css";

export const MovieForm = (props) => {
  const { id, title, URL, releaseDate, timeDuration, gener, age } = props;

  const [ID, setID] = useState(id);

  if (ID === "") {
    setID(Date.now());
  }

  const SignupSchema = Yup.object().shape({
    firstname: Yup.string()
      .min(2, "Too Short!")
      .max(20, "Too Long!")
      .required("Required"),
    lastname: Yup.string()
      .min(2, "Too Short!")
      .max(20, "Too Long!")
      .required("Required"),
    phonenumber: Yup.string()
      .matches(phoneRegExp, "Invalid Number")
      .required("Required"),
    email: Yup.string().email().required("Required"),
    password: Yup.string().required("Required").min(8).required("Required"),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref("password"), null], "Passwords must match")
      .required("Required"),
  });

  const formik = useFormik({
    initialValues: {
      id: ID,
      title: "",
      releaseDate: "",
      timeDuration: "",
      gener: "",
      age: "",
      URL: "",
    },
    validationSchema: SignupSchema,
    onSubmit: (values) => {
      console.log(values);
    },
  });

  return (
    <div className="Movie-Form">
      <AiOutlineClose
        className="form-close"
        onClick={() => props.show(false)}
      />
      <h1 className="form-name">Add Movie</h1>
      <form action="" className="movie-info" onSubmit={formik.handleSubmit}>
        <div className="movie-title info">
          <span>Title</span>
          <input
            type="text"
            name="title"
            onChange={formik.handleChange}
            value={formik.values.title}
            placeholder="Enter movie name..."
          />
        </div>

        <div className="movie-release-date info">
          <span>Release Date</span>
          <input
            type="text"
            name="releaseDate"
            onChange={formik.handleChange}
            value={formik.values.releaseDate}
            placeholder="dd-mmm-yyyy"
          />
        </div>
        <div className="movie-time info">
          <span>Time Duration</span>
          <input
            type="text"
            name="timeDuration"
            onChange={formik.handleChange}
            value={formik.values.timeDuration}
            placeholder="00h 00m"
          />
        </div>
        <div className="movie-gener info">
          <span>Gener</span>
          <input
            type="text"
            name="gener"
            onChange={formik.handleChange}
            value={formik.values.gener}
            placeholder="Movie gener..."
          />
        </div>

        <div className="movie-age info">
          <span>Age Limit</span>
          <input
            type="text"
            name="age"
            onChange={formik.handleChange}
            value={formik.values.age}
            placeholder="Age+"
          />
        </div>

        <div className="movie-image-url info">
          <span>Image URL</span>
          <input
            type="text"
            name="URL"
            onChange={formik.handleChange}
            value={formik.values.URL}
            placeholder="Enter movie image url..."
          />
        </div>
      </form>
      <div className="add-movie-btn">
        <button
          onClick={() => {
            props.newMovieInfo(movieInfo, isFieldsEmpty());
            props.show(isFieldsEmpty());
          }}
        >
          <span>ADD MOVIE</span>
        </button>
      </div>
    </div>
  );
};
