import React from "react";
import "./signup.css";
import { useFormik } from "formik";
import * as Yup from "yup";
import { BiErrorCircle } from "react-icons/bi";

export const Signup = () => {
  const SignupSchema = Yup.object().shape({
    firstname: Yup.string().min(2).max(50).required(),
    lastname: Yup.string().min(2).max(50).required(),
    email: Yup.string().email().required(),
    password: Yup.string().required().min(8).required(),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref("password"), null])
      .required(),
  });

  const formik = useFormik({
    initialValues: {
      firstname: "",
      lastname: "",
      phonenumber: "",
      email: "",
      password: "",
      confirmPassword: "",
    },
    validationSchema: SignupSchema,
    onSubmit: (values) => {
      console.log(formik.errors);
    },
  });

  console.log(formik.errors);

  return (
    <div className="signup-background">
      <div className="signup-card">
        <div className="card-info"></div>
        <div className="card-form">
          <h1>Register</h1>
          <form action="" className="input-from" onSubmit={formik.handleSubmit}>
            <div className="form-fields">
              <div className="field fn">
                <span>First Name</span>
                <input
                  type="text"
                  name="firstname"
                  onChange={formik.handleChange}
                  value={formik.values.firstname}
                />

                {formik.errors.firstname && formik.touched.firstname ? (
                  <div>
                    <BiErrorCircle className="errorIcon" />
                    <div className="errorLine"></div>
                    <span className="errorText">{formik.errors.firstname}</span>
                  </div>
                ) : null}
              </div>

              <div className="field ln">
                <span>Last Name</span>
                <input
                  type="text"
                  name="lastname"
                  onChange={formik.handleChange}
                  value={formik.values.lastname}
                />
              </div>

              <div className="field pn">
                <span>Phone Number</span>
                <input
                  type="tel"
                  pattern="[0-9]{4}-[0-9]{7}"
                  name="phonenumber"
                  onChange={formik.handleChange}
                  value={formik.values.phonenumber}
                />
              </div>

              <div className="field em">
                <span>Email</span>
                <input
                  type="email"
                  name="email"
                  onChange={formik.handleChange}
                  value={formik.values.email}
                />
              </div>

              <div className="field ps">
                <span>Password</span>
                <input
                  type="password"
                  name="password"
                  onChange={formik.handleChange}
                  value={formik.values.password}
                />
              </div>

              <div className="field cps">
                <span>Confirm Password</span>
                <input
                  type="password"
                  name="confirmPassword"
                  onChange={formik.handleChange}
                  value={formik.values.confirmPassword}
                />
              </div>
            </div>
            <input className="submit" type="submit" value="Create Account" />
          </form>
          <div className="login-link">
            <span>Already have an account? </span>
            <h5>Log in</h5>
          </div>
        </div>
      </div>
    </div>
  );
};
