import React, { useContext } from "react";
import "./detailPage.css";
import { MdDelete, MdEdit } from "react-icons/md";
import { RootContext } from "../../Context/rootContext";

export const DetailPage = (props) => {
  const { movie, setMovie } = useContext(RootContext);
  props = movie;
  const { id, title, URL, releaseDate, timeDuration, gener, age } = props;

  return (
    <div className="page">
      <div className="dp-card">
        <div className="dp-card-image">
          <img src={URL} alt="" />
        </div>

        <div className="dp-card-detail">
          <div className="dp-h1-btn">
            <span>{title}</span>
            <div className="dp-card-buttons">
              <div className="dp-card-btn-ring dp-card-edit-ring">
                <MdEdit
                  className="dp-card-btn "
                  onClick={() => props.edit(id)}
                />
              </div>
              <div className="dp-card-btn-ring">
                <MdDelete
                  className="dp-card-btn card-delete"
                  onClick={() => props.delete(id)}
                />
              </div>
            </div>
          </div>

          <div className="dp-card-d1">
            <span>{releaseDate}</span>
            <div className="dp-card-age-icon">
              <span>{age}</span>
            </div>
            <p>{timeDuration}</p>
            <div className="dp-card-hd-icon">
              <span>HD</span>
            </div>
          </div>

          <div className="dp-card-d2">
            <span>{gener}</span>
          </div>
        </div>
      </div>
    </div>
  );
};
